import React from "react";
import { NavLink, Route, Switch } from "react-router-dom";


function FormationCard({ formation, setFormationUrl }) {

    function handleLinkClick() {
        return setFormationUrl(formation.uniqueid);
    };

    return (
        <div
            className="formation-card"
            style={{ background: `${formation.color}` }}
        >
            <img className="formation-image" src={formation.image} alt={`${formation.name} picture`} />
            <h2>{formation.name}</h2>
            <div>
                <NavLink onClick={handleLinkClick} className="formation-link" to={`/formations/${formation.uniqueid}/users`}>Users</NavLink>
                <NavLink onClick={handleLinkClick} className="formation-link" to={`/formations/${formation.uniqueid}/info`}>Shared Information</NavLink>
                <NavLink onClick={handleLinkClick} className="formation-link" to={`/formations/${formation.uniqueid}/settings`}>Settings</NavLink>
            </div>
        </div>
    );
};

export default FormationCard;
