import React from "react";


function Requests({ currentUser, userData }) {

    if (currentUser.requests.length == 0) {
        return (
            <div id="formation-array-container">
                <h1>Requests to Join Formations</h1>
                <h3>No requests at this time.</h3>
            </div>
        );
    } else {
        const requestDisplay = currentUser.requests.map((el) => <RequestCard currentUser={currentUser} request={el} userData={userData} />);
        return (
            <div id="formation-array-container">
                <h1>Requests to Join Formations</h1>
                {requestDisplay}
            </div>
        );
    }    
};

export default Requests;
