import React, { useState } from "react";
import { useHistory } from "react-router-dom";

function storeToken(userToken) {
    return sessionStorage.setItem('token', JSON.stringify(userToken));
};

function SignUp({ userData, isLoaded, setCurrentUser }) {
    const [username, setUserName] = useState("");
    const [password, setPassword] = useState("");

    const history = useHistory();

    function handleSubmit(e) {
        e.preventdefault();
        const userMatch = userData.filter((user) => user.username === username);
        if (userMatch[0].password === password) {
            storeToken(userMatch[0].token);
            setCurrentUser(userMatch[0]);
            return history.push("/formations");
        } else {
            return alert("Sorry, incorrect login information");
        }
    };

    return (
        <div className="login-wrapper">
            <h1>Please Sign Up</h1>
            <form onSubmit={handleSubmit}>
                <label>
                    <p>Username</p>
                    <input type="text" onChange={e => setUserName(e.target.value)}/>
                </label>
                <label>
                    <p>Password</p>
                    <input type="password" onChange={e => setPassword(e.target.value)}/>
                </label>
                <div>
                    <button type="submit">Sign Up</button>
                </div>
            </form>
        </div>
    );
};

export default SignUp;
