import React, { useState, useEffect } from "react";

function FormationSettings({ currentUser, formationId, patchUser, updateCurrentUser, userData, updateUserData }) {

    const currentFormationArr = currentUser.formations.filter((formation) => formation.uniqueid === formationId);

    const currentFormation = currentFormationArr[0];

    return (
        <div id="formation-array-container">
            <h1>Settings for {currentFormation.name}</h1>
            <form>
                <input type="text" name="formationname" placeholder={currentFormation.name} />
                <input type="color" name="formationcolor" />
                <input type="url" name="formationimage" placeholder="image url" />
                <input type="submit" />
            </form>

        </div>
    );
};

export default FormationSettings;
