import React from "react";
import { Link } from "react-router-dom";

function Login() {
    return (
        <div>
            <h1>Login</h1>
            <form>
                <div>
                    <input type="text" name="username" placeholder="Username" />
                </div>
                <div>
                    <input type="password" name="password" placeholder="Password" />
                </div>
                <input type="submit" value="Submit" />
            </form>
            <p>Not a current user? <Link to={`/signup`}>Sign up</Link></p>
        </div>
    );
};

export default Login;

/*
on submit, send login information up to app
using information from app, verify that there is a user with a matching username and password
if there is not, clear form and issue alert: "Sorry, no such username password combination"
if there is, use programatic routing to take user to contactcontainer
*/
