import React, { useState } from "react";


function FormationInfo({ currentUser, formationId, token }) {



    const [pronounsShared, setPronounsShared] = useState("unshared");
    const [emailShared, setEmailShared] = useState("unshared");
    const [phoneShared, setPhoneShared] = useState("unshared");
    const [addressShared, setAddressShared] = useState("unshared");
    const [notesShared, setNotesShared] = useState("unshared");

    if (token) {

        const currentFormation = currentUser.formations.filter((formation) => {
            return formation.uniqueid === formationId;
        });

        currentFormation.pronouns === "shared" ? setPronounsShared("shared") : setPronounsShared("unshared");
        currentFormation.email === "shared" ? setEmailShared("shared") : setPronounsShared("unshared");
        currentFormation.phone === "shared" ? setPhoneShared("shared") : setPronounsShared("unshared");
        currentFormation.address === "shared" ? setAddressShared("shared") : setPronounsShared("unshared");
        currentFormation.notes === "shared" ? setNotesShared("shared") : setPronounsShared("unshared");
    }

    return (
        <div id="formation-array-container">
            <h1>Shared Information</h1>
            <div>
                <p><b>Pronouns</b></p>
                <div onChange={e => setPronounsShared(e.target.value)}>
                    <input type="radio" id="pronounshared" name="pronouns" value="shared" checked={e => e.target.value === pronounsShared} />
                    <label htmlFor="pronounshared">Shared</label>
                    <input type="radio" id="pronounsunshared" name="pronouns" value="unshared" checked={e => e.target.value === pronounsShared} />
                    <label htmlFor="pronounsunshared">Unshared</label>
                </div>
            </div>
        </div>
    );
};

export default FormationInfo;
