import React, { useState, useEffect } from "react";
import { Route, Switch, useHistory } from "react-router-dom";
import Login from "./Login.js";
import Logout from "./Logout.js";
import SignUp from "./SignUp.js";
import Banner from "./Banner.js";
import PersonalInfo from "./PersonalInfo.js";
import Formations from "./Formations.js";
import Home from "./Home.js";

function App() {
    const [currentUser, setCurrentUser] = useState();
    const [userData, setUserData] = useState([]);
    const [isLoaded, setIsLoaded] = useState(false);
    const [token, setToken] = useState(false);

    const history = useHistory();

    useEffect(() => {
        return fetch("http://localhost:3000/users")
        .then((response) => response.json())
        .then((data) => setUserData(data))
        .then(() => setIsLoaded(true))
    }, []);

    useEffect(() => {
        return fetch("http://localhost:3000/users")
        .then((response) => response.json())
        .then((data) => setUserData(data))
        .then(() => setIsLoaded(true))
    }, []);

    useEffect(() => getToken(), 3);

    function getToken() {
        const tokenString = sessionStorage.getItem('token');
        const userToken = JSON.parse(tokenString);
        console.log(userToken)
        console.log(userData)
        if (userToken) {
            const returningUser = userData.filter((user) => user.token.username === userToken.username && user.token.password === userToken.password);
            //setCurrentUser(returningUser);
            console.log(returningUser)
            return returningUser[0].length > 0 ? setToken(true) : setToken(false);
            return setToken(false);
        }
    };

    function logIn(userObj) {
        setCurrentUser(userObj);
        sessionStorage.setItem('token', JSON.stringify(userObj.token));
        return history.push("/personalinfo");
    };

    function logOut() {
        sessionStorage.clear();
        setCurrentUser();
        return history.push("/personalinfo");
    };

    return (
        <div>
            <Banner token={token} logOut={logOut} isLoaded={isLoaded} />
            <Switch>
                <Route path="/login">
                    <Login setToken={setToken} />
                </Route>
                <Route path="/logout">
                    <Logout />
                </Route>
                <Route path="/formations">
                    <Formations />
                </Route>
                <Route path="/personalinfo">
                    <PersonalInfo />
                </Route>
                <Route path="/signup">
                    <SignUp userData={userData} isLoaded={isLoaded} setCurrentUser={setCurrentUser} logIn={logIn} />
                </Route>
                <Route path="/">
                    <Home />
                </Route>
            </Switch>
        </div>
    );
}

export default App;
