import React from "react";

function ContactCard({ user, privNotes, circle }) {

    function handlePrivNoteChange(e) {
        e.preventDefault();
    };

    function handleCircleChange(e) {
        e.preventDefault();
    };

    function handleDelete(e) {
        e.preventDefault();
    };

    return (
        <div className="contact-card">
            <h3>{user.name}</h3>
            <p>{user.pronouns}</p>
            <p>{user.dob}</p>
            <img src={user.pic} alt={`${user.name} picture`} />
            <p>{user.email}</p>
            <p>{user.phone}</p>
            <p>{user.address}</p>
            <p>Their Notes: {user.addnotes}</p>
            <form onSubmit={handlePrivNoteChange}>
                <label htmlFor="priv-notes">Your Notes:</label>
                <input type="textbox" name="priv-notes" defaultValue={privNotes}></input>
                <input type="submit" />
            </form>
            <form onSubmit={handleCircleChange}>
                <label htmlFor="circle">Circle:</label>
                <input type="select" name="circle" defaultValue={circle}>
                    <option value="family">Family</option>
                    <option value="friends">Friends</option>
                    <option value="collegues">Collegues</option>
                </input>
                <input type="submit" value="Submit Changes" />
            </form>
            <form onSubmit={handleDelete}>
                <input type="submit" value="Delete Contact" /> 
            </form>
        </div>
    );
};

export default ContactCard;
