import React from "react";
import { SideNavBar } from "./Navbar";
import ContactCard from "./ContactCard";

function MyContactCard({ currentUser, handleUserUpdate }) {

    function handleMyInfoSubmit(e) {
        e.preventDefault();
        const nameInput = e.target.firstChild.nextSibling;
        const dobInput = nameInput.nextSibling.nextSibling;
        const pronounsInput = dobInput.nextSibling.nextSibling;
        const phoneNoInput = pronounsInput.nextSibling.nextSibling;
        const emailInput = phoneNoInput.nextSibling.nextSibling;
        const addressInput = emailInput.nextSibling.nextSibling;
        const picUrlInput = addressInput.nextSibling.nextSibling;
        const notesInput = picUrlInput.nextSibling.nextSibling;
        const usernameInput = notesInput.nextSibling.nextSibling;
        const passwordInput = usernameInput.nextSibling.nextSibling;
        const newUserObj = {
            username: usernameInput.value,
            password: passwordInput.value,
            name: e.target[1].value,
            dob: dobInput.value,
            pic: picUrlInput.value,
            pronouns: pronounsInput.value,
            phone: phoneNoInput.value,
            email: emailInput.value,
            address: addressInput.value,
            addnotes: notesInput.value
        };
        fetch(`http://localhost:3000/users/${currentUser.id}`, {
            method: "PATCH",
            headers: {
                "Content-Type": "application/json",
                Accept: "application/json"
            },
            body: JSON.stringify(newUserObj)
        })
        .then((response) => response.json())
        .then((data) => handleUserUpdate(data))
    };

    return (
        <div>
            <SideNavBar />
            <h1>My Contact Card</h1>
            <ContactCard user={currentUser} />
            <form id="mycontactform" onSubmit={handleMyInfoSubmit}>
                <label htmlFor="name">Name</label>
                <input type="text" name="name" defaultValue={currentUser.name}></input>
                <label htmlFor="dob">Birthday</label>
                <input type="date" name="dob" defaultValue={currentUser.dob}></input>
                <label htmlFor="pronouns">Pronouns</label>
                <input type="text" name="pronouns" defaultValue={currentUser.pronouns}></input>
                <label htmlFor="phone1no">Phone Number</label>
                <input type="tel" name="phoneno" defaultValue={currentUser.phoneno}></input>
                <label htmlFor="email">Email Address</label>
                <input type="email" name="email" defaultValue={currentUser.email}></input>
                <label htmlFor="address">Address</label>
                <input type="textbox" name="address" defaultValue={currentUser.address}></input>
                <label htmlFor="picurl">Picture Url</label>
                <input type="url" name="picurl" defaultValue={currentUser.picurl}></input>
                <label htmlFor="addnotes">Additional Notes</label>
                <input type="textbox" name="addnotes" defaultValue={currentUser.addnotes}></input>
                <label htmlFor="username">Username</label>
                <input type="text" name="username" autoComplete="username" defaultValue={currentUser.username}></input>
                <label htmlFor="password">Password</label>
                <input type="password" name="password" autoComplete="password" defaultValue={currentUser.password}></input>
                <input type="submit"></input>
            </form>
        </div>
    );
};

export default MyContactCard;
