import React, { useEffect } from "react";
import FormationCard from "./FormationCard.js"


function MyFormations({ currentUser, searchValue, userData, patchCurrentUser,isLoaded }) {

    if (currentUser.formations.length > 0) {
        removeInactiveFormations();

        const displayFormations = currentUser.formations.map((el1) => <FormationCard key={el1.id} formation={el1} currentUser={currentUser} userData={userData}/>)

        function removeInactiveFormations(){
            const inactiveFormations = currentUser.formations.filter((el1) => {
                const owner = userData.find((el2) => el2.id === el1.admin);
                const ownerFormArr = owner.formations.map((el2) => el2.id);
                return !ownerFormArr.includes(el1.id);
            });
            const inactiveFormationsIdArr = inactiveFormations.map((el) => el.id);
            const sansFormations = currentUser.formations.filter((el) => !inactiveFormationsIdArr.includes(el.id));
            return patchCurrentUser({formations: sansFormations});
        };

        return (
            <div>
                <h1>My Formations</h1>
                <div id="formation-array-container">
                    {displayFormations}
                </div>
            </div>
        );
    } else {
        return (
            <div>
                <h1>My Formations</h1>
                <div id="formation-array-container">
                    <h2>No formations yet.</h2>
                </div>
            </div>
        );
    }


};

export default MyFormations;
