import React, { useState } from "react";
import CircleSelect from "./CircleSelect";

function ContactCard({ user, notes, circle, currentUser, updatePrivateNote, updateCircle, deleteContact, search, makeRequest, filterCircle, isLoaded }) {

    const [privateNotes, setPrivateNotes] = useState(notes);
    const [currentCircle, setCurrentCircle] = useState(circle);


    function handlePrivNoteSubmit(e) {
        e.preventDefault();
        return updatePrivateNote(e.target.privnotes.value, currentUser, user.id);
    };

    function handleCircleSubmit(e) {
        e.preventDefault();
        return updateCircle(e.target.circle.value, currentUser, user.id);
    };

    function handleDeleteSubmit(e) {
        e.preventDefault();
        return deleteContact(currentUser, user.id);
    };

    function handleNoteChange(e) {
        return setPrivateNotes(e.target.value);
    };

    function handleCircleChange(e) {
        return setCurrentCircle(e.target.value);
    };

    function handleRequestClick(e) {
        const requestedCircle = e.target.previousSibling.value;
        return makeRequest(currentUser, user, requestedCircle);
    };

    if (isLoaded) {
        const informationDisplay = [];

        if (user !== currentUser) {
            let proxy = 0;
            const userContact = user.contacts.filter((contact) => contact.contactId === currentUser.id);
            console.log(user.contacts.filter((contact) => contact.contactId === currentUser.id))
            const userCircle = userContact[0].circle;
            user.pronounsfilter.includes(userCircle) ? informationDisplay.push(<p>Pronouns: {user.pronouns}</p>) : proxy = 0;
            user.dobfilter.includes(userCircle) ? informationDisplay.push(<p>Date of Birth: {user.dob}</p>) : proxy = 0;
            informationDisplay.push(<img src={user.pic} alt={`${user.name} picture`} />);
            user.emailfilter.includes(userCircle) ? informationDisplay.push(<p>Email: {user.email}</p>) : proxy = 0;
            user.phonefilter.includes(userCircle) ? informationDisplay.push(<p>Phone: {user.phone}</p>) : proxy = 0;
            user.addressfilter.includes(userCircle) ? informationDisplay.push(<p>Address: {user.address}</p>) : proxy = 0;
            user.addnotesfilter.includes(userCircle) ? informationDisplay.push(<p>Public Notes: {user.addnotes}</p>) : proxy = 0;
        }

        if (search) {
            return (
                <div className="contact-card">
                    <h3>{user.name}</h3>
                    <p>{user.username}</p>
                    <img src={user.pic} alt={`${user.name} picture`} />
                    <select name="circle">
                        <option value="family">Family</option>
                        <option value="friends">Friends</option>
                        <option value="collegues">Collegues</option>
                    </select>
                    <button onClick={handleRequestClick}>Request Information</button>
                </div>
            );
        } else if (user !== currentUser) {
            return (
                <div className="contact-card">
                    {informationDisplay}
                    <form onSubmit={handlePrivNoteSubmit}>
                        <label htmlFor="privnotes">Your Notes:</label>
                        <textarea name="privnotes" value={privateNotes} onChange={handleNoteChange} />
                        <input type="submit" value="Submit Changes" />
                    </form>
                    <form onSubmit={handleCircleSubmit}>
                        <label htmlFor="circle">Circle:</label>
                        <select name="circle" value={currentCircle} onChange={handleCircleChange}>
                            <option value="family">Family</option>
                            <option value="friends">Friends</option>
                            <option value="collegues">Collegues</option>
                        </select>
                        <input type="submit" value="Submit Changes" />
                    </form>
                    <form onSubmit={handleDeleteSubmit}>
                        <input type="submit" value="Delete Contact" /> 
                    </form>
                </div>
            );
        } else {
            return (
                <div className="contact-card">
                    <h3>{user.name}</h3>
                    <p>{user.username}</p>
                    <p>Pronouns: {user.pronouns}</p>
                    <CircleSelect currentUser={currentUser} information={"pronouns"} filterCircle={filterCircle} /> 
                    <p>Date of Birth: {user.dob}</p>
                    <CircleSelect currentUser={currentUser} information={"dob"} filterCircle={filterCircle} /> 
                    <img src={user.pic} alt={`${user.name} picture`} />
                    <p>Email: {user.email}</p>
                    <CircleSelect currentUser={currentUser} information={"email"} filterCircle={filterCircle} /> 
                    <p>Phone: {user.phone}</p>
                    <CircleSelect currentUser={currentUser} information={"phone"} filterCircle={filterCircle} /> 
                    <p>Address: {user.address}</p>
                    <CircleSelect currentUser={currentUser} information={"address"} filterCircle={filterCircle} /> 
                    <p>Public Notes: {user.addnotes}</p>
                    <CircleSelect currentUser={currentUser} information={"addnotes"} filterCircle={filterCircle} /> 
                </div>
            );
        }
    } else {
        return <h1>Loading...</h1>;
    }

};

export default ContactCard;
