import React, { useState } from "react";
import { Redirect, Route, Switch } from "react-router-dom";
import MyFormations from "./MyFormations.js";
import NewFormation from "./NewFormation.js";
import FormationBar from "./FormationBar.js";
import Invitations from "./Invitations.js";
import Requests from "./Requests.js";
import FormationUsers from "./FormationUsers.js";
import FormationInfo from "./FormationInfo.js";
import FormationSettings from "./FormationSettings.js";

function Formations({ token, currentUser, patchUser, updateCurrentUser, userData }) {

    const [formationUrl, setFormationUrl] = useState();
    const formationIds = currentUser.formations.map((formation) => {
        return formation.uniqueId;
    });
    console.log(formationIds)

    const formationUsers = formationIds.map((id) => {
        return (
            <Route path={`/formations/${id}/users`}>
                <FormationUsers />
            </Route> 
        );
    });

    const formationInfo = formationIds.map((id) => {
        return (
            <Route path={`/formations/${id}/info`}>
                <FormationInfo />
            </Route> 
        );
    });

    const formationSettings = formationIds.map((id) => {
        return (
            <Route path={`/formations/${id}/settings`}>
                <FormationSettings />
            </Route> 
        );
    });


    if (token === "valid") {
        return (
            <div className="display-container">
                <h1>Formations</h1>
                <FormationBar />
                <Switch>
                    <Route path="/formations/invitations">
                        <Invitations />
                    </Route>
                    <Route path="/formations/requests">
                        <Requests />
                    </Route>
                    <Route path="/formations/newformation">
                        <NewFormation />
                    </Route>
                    {formationUsers}
                    {formationInfo}
                    {formationSettings}
                    <Route path="/formations">
                        <MyFormations
                            currentUser={currentUser}
                            setFormationUrl={setFormationUrl} 
                        />
                    </Route>
                </Switch>
            </div>
        );
    } else if (token === "invalid") {
        return <Redirect to="/login" />;
    } else {
        return <h1>Loading...</h1>;
    }};

export default Formations;
