import React, { useState, useEffect } from "react";
import { Route, Switch, useHistory } from "react-router-dom";
import Login from "./Login.js";
import SignUp from "./SignUp.js";
import Banner from "./Banner.js";
import PersonalInfo from "./PersonalInfo.js";
import Formations from "./Formations.js";
import Home from "./Home.js";

function App() {
    const [currentUser, setCurrentUser] = useState();
    const [userData, setUserData] = useState([]);
    const [token, setToken] = useState(false);
    const [isLoaded, setIsLoaded] = useState(false);

    const history = useHistory();

    useEffect(() => {
        return fetch("http://localhost:3000/users")
        .then((response) => response.json())
        .then((data) => setUserData(data))
        .then(() => setIsLoaded(true))
    }, []);

    useEffect(() => getToken(), [userData]);

    function getToken() {
        const tokenString = sessionStorage.getItem('token');
        const userToken = JSON.parse(tokenString);
        if (isLoaded && userToken) {
            const returningUser = userData.filter((user) => user.token.username === userToken.username && user.token.password === userToken.password);
            setCurrentUser(returningUser[0]);
            return returningUser.length > 0 ? setToken(true) : setToken(false);
        } else {
            return setToken(false);
        }
    };

    function logIn(userObj) {
        setCurrentUser(userObj);
        console.log(userObj)
        sessionStorage.setItem('token', JSON.stringify(userObj.token));
        setToken(true);
        return history.push("/personalinfo");
    };

    function logOut() {
        sessionStorage.clear();
        setToken(false);
        setCurrentUser();
        return history.push("/");
    };

    function patchUser() {
        return console.log("patch")
    };

    function updateCurrentUser() {
        return console.log("update")
    };

    return (
        <div>
            <Banner
                token={token}
                logOut={logOut}
                isLoaded={isLoaded}
            />
            <Switch>
                <Route path="/login">
                    <Login
                        userData={userData}
                        setToken={setToken}
                        logIn={logIn}
                    />
                </Route>
                <Route path="/formations">
                    <Formations
                        isLoaded={isLoaded}
                        currentUser={currentUser}
                    />
                </Route>
                <Route path="/personalinfo">
                    <PersonalInfo
                        currentUser={currentUser}
                        token={token}
                        isLoaded={isLoaded}
                        patchUser={patchUser}
                        updateCurrentUser={updateCurrentUser}
                    />
                </Route>
                <Route path="/signup">
                    <SignUp
                        userData={userData}
                        isLoaded={isLoaded}
                        setCurrentUser={setCurrentUser}
                        logIn={logIn}
                    />
                </Route>
                <Route path="/">
                    <Home
                        isLoaded={isLoaded}
                    />
                </Route>
            </Switch>
        </div>
    );
}

export default App;
