import React from "react";
import ContactCard from "./ContactCard";

let searchDisplay = <h2>Loading...</h2>;

function AddContact({ currentUser, isLoaded, userData, searchValue, handleRequest }) {

    if (isLoaded) {
        if (currentUser.requests[0] === undefined) {
            searchDisplay = <h2>No requests right now</h2>;
        } else {
            const searchedUsers = userData.filter((user) => {
                return user.username.toLowerCase().includes(searchValue.toLowerCase());
            });
            searchDisplay = searchedUsers.map((searchedUser) => {
                return (
                    <ContactCard
                        key={`${searchedUser.username}card`}
                        user={searchedUser}
                        currentUser={currentUser}
                        handleRequest={handleRequest}
                        search={true}
                    />
                );
            });
        }
    }

    return (
        <div>
            <h1>Add Contact</h1>
            {searchDisplay}
        </div>
    );
};

export default AddContact;
