import React, { useState } from "react";


function FormationInfo({ currentUser, formationId, token }) {

    const currentFormation = currentUser.formations.filter((formation) => {
        return formation.uniqueid === formationId;
    });

    const [pronounsShared, setPronounsShared] = useState("unshared");
    const [emailShared, setEmailShared] = useState("unshared");
    const [phoneShared, setPhoneShared] = useState("unshared");
    const [addressShared, setAddressShared] = useState("unshared");
    const [notesShared, setNotesShared] = useState("unshared");

    if (token) {
        currentFormation.pronouns === shared ? setPronounsShared("shared") : setPronounsShared(false);
        currentFormation.email === shared ? setEmailShared("shared") : setPronounsShared(false);
        currentFormation.phone === shared ? setPhoneShared("shared") : setPronounsShared(false);
        currentFormation.address === shared ? setAddressShared("shared") : setPronounsShared(false);
        currentFormation.notes === shared ? setNotesShared("shared") : setPronounsShared(false);
    }



    function checkChecked(e) {
        if (currentUser !== undefined) {
            const currentFormation = currentUser.formations.filter((formation) => {
                return formation.uniqueid === formationId;
            });
            const infoType = e.target.name;
            if (currentFormation.information.infoType === "shared" && e.target.value === "shared") {
                return true;
            } else if (currentFormation.information.infoType === "unshared" && e.target.value === "unshared") {
                return true;
            } else {
                return false;
            }
        } else if (e.target.value === "unshared") {
            return true;
        }
    };

    return (
        <div id="formation-array-container">
            <h1>Shared Information</h1>
            <div>
                <p><b>Pronouns</b></p>
                <div>
                    <input type="radio" id="pronounshared" name="pronouns" value="shared" checked={checkChecked} />
                    <label htmlFor="pronounshared">Shared</label>
                    <input type="radio" id="pronounsunshared" name="pronouns" value="unshared" checked={checkChecked} />
                    <label htmlFor="pronounsunshared">Unshared</label>
                </div>
            </div>
        </div>
    );
};

export default FormationInfo;
