import React from "react";

function FormationCard({ formation, currentUser }) {

    function handleFormationCardClick() {
        console.log(formation.uniqueid)
    };

    return (
        <div
            className="formation-card"
            style={{ background: `${formation.color}` }}
            onClick={handleFormationCardClick}
        >
            <img className="formation-image" src={formation.image} alt={`${formation.name} picture`} />
            <h2>{formation.name}</h2>
            <div>
                <h3>Share...</h3>
                <div>
                    <p><b>Email?</b></p>
                    <div>
                        <input type="radio" id="emailyes" name="email" value="emailshare" />
                        <label for="emailyes">Yes</label>
                        <input type="radio" id="emailno" name="email" value="emailnoshare" defaultChecked />
                        <label for="emailno">No</label>
                    </div>
                </div>
                <p><b>Phone?</b></p>
                <p><b>Address?</b></p>
                <p><b>Notes?</b></p>
            </div>

        </div>
    );
};

export default FormationCard;
