import React from "react";
import FormationCard from "./FormationCard.js"

function MyFormations({ currentUser, searchValue, userData, patchCurrentUser }) {

    let displayFormations = <h2>No formations yet.</h2>

    if (currentUser.formations.length > 0) {
        displayFormations = currentUser.formations.map((el1) => {
        const owner = userData.find((el2) => el2.id === el1.admin);
        const ownerFormArr = owner.formations.map((el2) => el2.id);
        if (ownerFormArr.includes(el1.id)) {
            return <FormationCard key={el.id} formation={el} currentUser={currentUser} userData={userData} />;
        } else {
            const sansCurrentFormationArr = currentUser.formations.filter((el2) => el2.id !== el1.id);
            return patchCurrentUser({formations: sansCurrentFormationArr});
        }})
    }

    return (
        <div>
            <h1>My Formations</h1>
            <div id="formation-array-container">
                {displayFormations}
            </div>
        </div>
    );
};

export default MyFormations;
