import React, { useState } from "react";
import { useHistory } from "react-router-dom";

function storeToken(userToken) {
    return sessionStorage.setItem('token', JSON.stringify(userToken));
};

function SignUp({ userData, setCurrentUser }) {
    const [username, setUserName] = useState("");
    const [password, setPassword] = useState("");

    const history = useHistory();

    function handleSubmit(e) {
        e.preventdefault();
        const userMatch = userData.filter((user) => user.username === username);
        if (userMatch) {
            return alert("Sorry, this username is already taken.");
        } else {
            createUser(username, password);
            return history.push("/personalinfo");
        };
    };

    function createUser(username, password) {
        const userObj = {
            token: {username: username, password: password},
            username: username,
            password: password,
            name: "",
            pronouns: "",
            picture: "",
            email: "",
            phone: "",
            address: "",
            formations: []
        };
        return (fetch("http://localhost:3000/users", {
              method: 'POST',
              headers: {
                'Content-Type': 'application/json'
              },
              body: JSON.stringify(userObj)
        })
        .then((response) => response.json())
        .then((data) => completSignUp(data)));
    };

    function completeSignUp(userObj) {
        storeToken(userObj.token);
        return setCurrentUser(userObj);
    };

    return (
        <div className="login-wrapper">
            <h1>Please Sign Up</h1>
            <form onSubmit={handleSubmit}>
                <label>
                    <p>Username</p>
                    <input type="text" onChange={e => setUserName(e.target.value)}/>
                </label>
                <label>
                    <p>Password</p>
                    <input type="password" onChange={e => setPassword(e.target.value)}/>
                </label>
                <div>
                    <button type="submit">Sign Up</button>
                </div>
            </form>
        </div>
    );
};

export default SignUp;
