import React, { useState } from "react";

function storeToken(token) {
    return sessionStorage.setItem('token', JSON.stringify(userToken));
};

function Login({ userData, isLoaded }) {
    const [username, setUserName] = useState("");
    const [password, setPassword] = useState("");

    function handleSubmit(e) {
        e.preventdefault();
        const userMatch = userData.filter((user) => user.username === username);
        if (userMatch[0].password === password) {
            storeToken(userMatch.token);
            return ;
        } else {
            alert("Sorry, incorrect login information");
        }
    }


    return(
        <div className="login-wrapper">
            <h1>Please Log In</h1>
            <form onSubmit={handleSubmit}>
                <label>
                    <p>Username</p>
                    <input type="text" onChange={e => setUserName(e.target.value)}/>
                </label>
                <label>
                    <p>Password</p>
                    <input type="password" onChange={e => setPassword(e.target.value)}/>
                </label>
                <div>
                    <button type="submit">Submit</button>
                </div>
            </form>
        </div>
    )
}

export default Login;
