import React from "react";
import UserCard from "./UserCard";

function FormationUsers({ currentUser, userData, formationId, patchCurrentUser }) {

    const currentFormationArr = currentUser.formations.filter((formation) => formation.id === formationId);

    const currentFormation = currentFormationArr[0];

    const formationUserArr = userData.filter((user) => currentFormation.users.includes(user.id));

    const displayUserCards = formationUserArr.map((user) => {
        return <UserCard key={`${user.token.username}card`} user={user} formationId={formationId} currentUser={currentUser} currentFormation={currentFormation} patchCurrentUser={patchCurrentUser} />
    });

    if (currentUser.id === currentFormation.admin) {
        return (
            <div id="formation-array-container">
                <h1>Users in {currentFormation.name}</h1>
                {displayUserCards}
            </div>
        );
    } else {
        return (
            <div id="formation-array-container">
                <h1>Owner of {currentFormation.name}</h1>
                {displayOwnerCard}
            </div>
        );
    }

};

export default FormationUsers;
