import React, { useState } from "react";
import { Redirect } from "react-router-dom";
import MyFormations from "./MyFormations.js";
import NewFormation from "./NewFormation.js";
import FormationBar from "./FormationBar.js";
import Invitations from "./Invitations.js";
import Requests from "./Requests.js";

function Formations({ token, currentUser, patchUser }) {
    const [formationPage, setFormationPage] = useState("myformations")

    let displayPage = <h1>Loading...</h1>;

    switch(formationPage) {
        case "myformations":
            displayPage = <MyFormations currentUser={currentUser} />;
            break;
        case "newformation":
            displayPage = <NewFormation currentUser={currentUser} patchUser={patchUser} />;
            break;
        case "invitations":
            displayPage = <Invitations currentUser={currentUser} />;
            break;
        case "requests":
            displayPage = <Requests currentUser={currentUser} />;
            break;
        default:
            displayPage = <MyFormations currentUser={currentUser} />;
            break;
    }

    if (token === "valid") {
        return (
            <div className="display-container">
                <h1>Formations</h1>
                <FormationBar setFormationPage={setFormationPage} />
                {displayPage}
            </div>
        );
    } else if (token === "invalid") {
        return <Redirect to="/login" />;
    } else {
        return <h1>Loading...</h1>;
    }};

export default Formations;
