import React, { useState, useEffect } from "react";
import { Route, Switch, useHistory } from "react-router-dom";
import About from "./About.js";
import AddContact from "./AddContact.js";
import ContactContainer from "./ContactContainer.js";
import ContactRequests from "./ContactRequests.js";
import Header from "./Header.js";
import Login from "./Login.js";
import MyContactCard from "./MyContactCard.js";
import SignUp from "./SignUp.js";

function App() {
    const [currentUser, setCurrentUser] = useState();
    const [userData, setUserData] = useState();
    const [isLoaded, setIsLoaded] = useState(false);

    const history = useHistory(); 

    useEffect(() => {
        fetch("http://localhost:3000/users")
        .then((response) => response.json())
        .then((data) => issueAccess(data))
        .then(() => setIsLoaded(true))
    }, []);

    function issueAccess(data) {
        setUserData(data);
        const accessUser = data.filter((user) => {
            return user.access;
        })
        return accessUser !== undefined ? setCurrentUser(accessUser[0]) : setCurrentUser({});
    };

    function handleLogIn(loggedUser) {
        userData.map((user) => {
            fetch(`http://localhost:3000/users/${user.id}`, {
                method: "PATCH",
                headers: {
                    "Content-Type": "application/json",
                    Accept: "application/json"
                },
                body: JSON.stringify({
                    access: false
                })
            })
        })
        fetch(`http://localhost:3000/users/${loggedUser.id}`, {
            method: "PATCH",
            headers: {
                "Content-Type": "application/json",
                Accept: "application/json"
            },
            body: JSON.stringify({
                access: true
            })
        })
        .then((response) => response.json())
        .then((data) => handleUserUpdate(data))
        .then(() => history.push('/mycard'))
    };

    function logOut() {
        userData.map((user) => {
            fetch(`http://localhost:3000/users/${user.id}`, {
                method: "PATCH",
                headers: {
                    "Content-Type": "application/json",
                    Accept: "application/json"
                },
                body: JSON.stringify({
                    access: false
                })
            })
        })
        history.push('/');
        return setCurrentUser();
    }

    function handleSignUp(user) {
        return setUserData([...userData, user]);
    }

    function handleUserUpdate(updatedUser) {
        setCurrentUser(updatedUser);
        const filteredUserData = userData.filter((user) => user.id !== updatedUser.id);
        return setUserData([...filteredUserData, updatedUser]);
    };

    function updatePrivateNote(newNote, currentUser, alteredContactId) {
        console.log(newNote, currentUser, alteredContactId)
        const oldContactsObj = currentUser.contacts;
        const filteredContacts = oldContactsObj.filter((contact) => contact.contactId !== alteredContactId);
        const updatedContact = oldContactsObj.filter((contact) => contact.contactId === alteredContactId);
        updatedContact.privatenotes = newNote;
        const newContactsObj = [...filteredContacts, updatedContact];
        console.log(newContactsObj)
        console.log(oldContactsObj)

        fetch(`http://localhost:3000/users/${currentUser.id}`, {
                method: "PATCH",
                headers: {
                    "Content-Type": "application/json",
                    Accept: "application/json"
                },
                body: JSON.stringify({
                    contacts: newContactsObj
                })
        })
        .then((response) => response.json())
        .then((data) => handleUserUpdate(data))
    };

    function updateCircle(newCircle, currentUser, alteredContactId) {
        const oldContactsObj = currentUser.contacts;
        const filteredContacts = oldContactsObj.filter((contact) => contact.contactId !== alteredContactId);
        const updatedContact = oldContactsObj.filter((contact) => contact.contactId === alteredContactId);
        updatedContact.circle = newCircle;
        const newContactsObj = [...filteredContacts, updatedContact];

        fetch(`http://localhost:3000/users/${currentUser.id}`, {
                method: "PATCH",
                headers: {
                    "Content-Type": "application/json",
                    Accept: "application/json"
                },
                body: JSON.stringify({
                    contacts: newContactsObj
                })
        })
        .then((response) => response.json())
        .then((data) => handleUserUpdate(data))
    };

    function deleteContact(currentUser, alteredContactId) {
        const oldContactsObj = currentUser.contacts;
        const filteredContacts = oldContactsObj.filter((contact) => contact.contactId !== alteredContactId);
        const newContactsObj = [...filteredContacts];

        fetch(`http://localhost:3000/users/${currentUser.id}`, {
                method: "PATCH",
                headers: {
                    "Content-Type": "application/json",
                    Accept: "application/json"
                },
                body: JSON.stringify({
                    contacts: newContactsObj
                })
        })
        .then((response) => response.json())
        .then((data) => handleUserUpdate(data))
    };

    return (
        <div>
            <Header currentUser={currentUser} logOut={logOut}/>
            <Switch>
                <Route path="/login">
                    <Login handleLogIn={handleLogIn} userData={userData}/>
                </Route>
                <Route path="/contacts">
                    <ContactContainer
                        currentUser={currentUser}
                        userData={userData}
                        isLoaded={isLoaded}
                        updatePrivateNote={updatePrivateNote}
                        updateCircle={updateCircle}
                        deleteContact={deleteContact}
                    />
                </Route>
                <Route path="/mycard">
                    <MyContactCard currentUser={currentUser} handleUserUpdate={handleUserUpdate} isLoaded={isLoaded}/>
                </Route>
                <Route path="/requests">
                    <ContactRequests currentUser={currentUser} isLoaded={isLoaded}/>
                </Route>
                <Route path="/addcontact">
                    <AddContact currentUser={currentUser} isLoaded={isLoaded}/>
                </Route>
                <Route path="/signup">
                    <SignUp handleLogIn={handleLogIn} handleSignUp={handleSignUp} userData={userData}/>
                </Route>
                <Route path="/">
                    <About />
                </Route>
            </Switch>
        </div>
    );
}

export default App;
