import React from "react";

function UserCard({ user, formationId }) {

    const userFormationObj = user.formations.filter((formation) => formation.uniqueid === formationId);

    const informationDisplay = [];

    if (userFormationObj.pronouns === "shared") {
        informationDisplay.push("pronouns")
    }
    if (userFormationObj.email === "shared") {
        informationDisplay.push("email")
    }
    if (userFormationObj.phone === "shared") {
        informationDisplay.push("phone")
    }
    if (userFormationObj.address === "shared") {
        informationDisplay.push("address")
    }
    if (userFormationObj.notes === "shared") {
        informationDisplay.push("notes")
    }

    console.log(informationDisplay)

    return (
        <div className="user-card">
            <img key={`${user.name}pic`} className="contact-image" src={user.pic} alt={`${user.name} picture`} />
            <div key={`${user.name}userinfo`} className="userinfo">
                <h3>{user.name}</h3>
                {informationDisplay}
            </div>
        </div>
    );
};

export default UserCard;
