import React from "react";


function FormationInfo({ currentUser, formationId }) {

    function checkChecked(e) {
        if (currentUser !== undefined) {
            const currentFormation = currentUser.formations.filter((formation) => {
                return formation.uniqueid === formationId;
            });
            const infoType = e.target.name;
            if (currentFormation.information.infoType === "shared" && e.target.value === "shared") {
                return true;
            } else if (currentFormation.information.infoType === "unshared" && e.target.value === "unshared") {
                return true;
            } else {
                return false;
            }
        } else if (e.target.value === "unshared") {
            return true;
        }
    };

    return (
        <div id="formation-array-container">
            <h1>Shared Information</h1>
            <div>
                <p><b>Pronouns</b></p>
                <div>
                    <input type="radio" id="pronounshared" name="pronouns" value="shared" checked={checkChecked} />
                    <label htmlFor="pronounshared">Shared</label>
                    <input type="radio" id="pronounsunshared" name="pronouns" value="unshared" checked={checkChecked} />
                    <label htmlFor="pronounsunshared">Unshared</label>
                </div>
            </div>
        </div>
    );
};

export default FormationInfo;
