import React, {useEffect} from "react";


function FormationUsers({ userData, formationId }) {

    let formationUserArr = [];

    useEffect(() => {
        const formationUserArr = userData.filter((user) => {
            const formationIdArr = user.formation.map((formation) => {
                return formation.uniqueid;
            });
            return formationIdArr.includes(formationId);
        })
    }, [userData]);


    console.log(formationUserArr)
    return (
        <div id="formation-array-container">
            <h1>Users</h1>
        </div>
    );
};

export default FormationUsers;
