import React from "react";
import { NavLink } from "react-router-dom";

const linkStyle = {
    margin: "1rem",
    textDecoration: "none",
    color: 'blue'
  };

function TopNavBar() {
    return (
        <div className="navbar" id="top-navbar">
            <NavLink to="/" exact style={linkStyle}>About</NavLink>
            <NavLink to="/login" exact style={linkStyle}>Login</NavLink>
            <NavLink to="/signup" exact style={linkStyle}>Sign-up</NavLink>
        </div>
    );
};

function SideNavBar({ search, logOut, currentUser }) {

    function handleLogOut() {
        return logOut();
    };

    function handleSearch(e) {
        e.preventDefault();
        return search(e.target.search.value);
    };

    return (
        <div className="navbar" id="side-navbar">
            <NavLink to="/contacts" exact style={linkStyle}>Contacts</NavLink>
            <NavLink to="/requests" exact style={linkStyle}>Contact Requests</NavLink>
            <NavLink to="/mycard" exact style={linkStyle}>My Information</NavLink>
            <button onClick={handleLogOut}>Log Out</button>
            <form onSubmit={handleSearch}>
                <label htmlFor="search">Search for User</label>
                <input type="text" name="search" />
                <input type="submit" />
            </form>
            <h3>Logged in as: {currentUser.username}</h3>
        </div>
    );
}

export { TopNavBar, SideNavBar };
