import React from "react";

function SignUp({ handleLogIn, userData }) {

    function submitSignUp(e) {
        e.preventDefault();
        const newUsername = e.target.firstChild.firstChild.value;
        const newPassword = e.target.firstChild.nextSibling.firstChild.value;
        const userMatch = userData.filter((user) => user.username === newUsername);
        userMatch.username === newUsername? alert("Username already in use") : createNewUser(newUsername, newPassword);
    };

    function createNewUser(newUsername, newPassword) {
        const newUserObj = {username: newUsername, password: newPassword}
        fetch("http://localhost:3000/users", {
            method: 'POST',
            headers: {
                "Content-Type": "application/json",
                Accept: "application/json"
            },
            body: JSON.stringify(newUserObj)
        })
        .then((response) => response.json())
        .then((data) => updateUsers(data))
    };

    function updateUsers(data) {
        handleSignUp(data);
        return handleLogIn(data.username, data.password);
    }

    return (
        <div>
            <h1>Sign Up</h1>
            <form onSubmit={submitSignUp}>
                <div>
                    <input type="text" name="username" placeholder="Username" />
                </div>
                <div>
                    <input type="password" name="password" placeholder="Password" />
                </div>
                <input type="submit" value="Submit" />
            </form>
        </div>
    );
};

export default SignUp;

/*
check against databas of users
if user with username, send alert and reset form
if no user with username, send alert, update loginData, and use programatic routing to go to ContactContainer
*/
